import 'package:flutter/material.dart';
import 'package:get/get.dart';
import 'package:smart_manager/app/modules/login/controllers/login_controller.dart';

class TestHiddenPassword extends GetView<LoginController> {
  const TestHiddenPassword({super.key});

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: Obx(
        () => TextFormField(
          obscureText: controller.isHidden.value,
          decoration: InputDecoration(
            suffix: IconButton(
              onPressed: () {
                controller.isHidden.toggle();
              },
              icon: const Icon(Icons.remove_red_eye),
            ),
          ),
        ),
      ),
    );
  }
}
